<?phpnamespace etichette;use seositiframework as ssf;/*** CREAZIONE DATABASE ***/function install_etichette_db(): bool{    try{                //TEMPLATE        $args = array(            ssf\getSingleField(DBT_NOME, ssf\Formato::TESTO(), ssf\Formato::FACOLTATIVO()),            ssf\getSingleField(DBT_TEM_TIPO, ssf\Formato::NUMERO(), ssf\Formato::OBBLIGATORIO()),            ssf\getSingleField(DBT_ID_ETI, ssf\Formato::NUMERO(), ssf\Formato::FACOLTATIVO())        );        ssf\creaTabella(DBT_TEM, $args);                //VOCE        $args = array(            ssf\getSingleField(DBT_VOC_LBL, ssf\Formato::TESTO(), ssf\Formato::OBBLIGATORIO()), //Label della voce            ssf\getSingleField(DBT_VOC_VALORE, ssf\Formato::TESTO(), ssf\Formato::FACOLTATIVO()), //valore della voce            ssf\getSingleField(DBT_VOC_TIPO, ssf\Formato::TESTO(), ssf\Formato::OBBLIGATORIO()), //tipo della voce (testuale o immagine)            ssf\getSingleField(DBT_ID_TEM, ssf\Formato::NUMERO(), ssf\Formato::OBBLIGATORIO()), //id del template            ssf\getSingleField(DBT_VOC_VIS, ssf\Formato::NUMERO(), ssf\Formato::OBBLIGATORIO()) //visualizzazione della label        );        $fks = array(            ssf\getFKs(DBT_ID_TEM, DBT_TEM)        );        ssf\creaTabella(DBT_VOC, $args, $fks);             //CATEGORIA        $args = array(            ssf\getSingleField(DBT_NOME, ssf\Formato::TESTO(), ssf\Formato::OBBLIGATORIO())        );        ssf\creaTabella(DBT_CAT, $args);                //CLIENTE        $args = array(            ssf\getSingleField(DBT_NOME, ssf\Formato::TESTO(), ssf\Formato::OBBLIGATORIO()),            ssf\getSingleField(DBT_ID_WP, ssf\Formato::NUMERO(), ssf\Formato::FACOLTATIVO())        );        ssf\creaTabella(DBT_CLI, $args);         //ETICHETTA        $args = array(            ssf\getSingleField(DBT_NOME, ssf\Formato::TESTO(), ssf\Formato::OBBLIGATORIO()),            ssf\getSingleField(DBT_ETI_DATA, ssf\Formato::DATA(), ssf\Formato::OBBLIGATORIO()),            ssf\getSingleField(DBT_ETI_URL, ssf\Formato::TESTO(), ssf\Formato::FACOLTATIVO()),            ssf\getSingleField(DBT_ID_CAT, ssf\Formato::NUMERO(), ssf\Formato::OBBLIGATORIO()),            ssf\getSingleField(DBT_ID_CLI, ssf\Formato::NUMERO(), ssf\Formato::FACOLTATIVO()),            ssf\getSingleField(DBT_ETI_LINK, ssf\Formato::TESTO(), ssf\Formato::OBBLIGATORIO()),            ssf\getSingleField(DBT_ETI_IMG, ssf\Formato::TESTO(), ssf\Formato::FACOLTATIVO())        );        $fks = array(            ssf\getFKs(DBT_ID_CAT, DBT_CAT)        );        ssf\creaTabella(DBT_ETI, $args, $fks);                //TRADUZIONE        $args = array(            ssf\getSingleField(DBT_TRA_LINGUA, ssf\Formato::TESTO(), ssf\Formato::OBBLIGATORIO())        );        ssf\creaTabella(DBT_TRA, $args);                //VOCE TRADOTTA        $args = array(            ssf\getSingleField(DBT_ID_VOC, ssf\Formato::NUMERO(), ssf\Formato::OBBLIGATORIO()),            ssf\getSingleField(DBT_VTR_LANG, ssf\Formato::TESTO(), ssf\Formato::OBBLIGATORIO()),            ssf\getSingleField(DBT_ID_TEM, ssf\Formato::NUMERO(), ssf\Formato::OBBLIGATORIO()),            ssf\getSingleField(DBT_VTR_LBL, ssf\Formato::TESTO(), ssf\Formato::OBBLIGATORIO()),            ssf\getSingleField(DBT_VTR_VAL, ssf\Formato::TESTO(), ssf\Formato::FACOLTATIVO()),            ssf\getSingleField(DBT_VTR_TIP, ssf\Formato::TESTO(), ssf\Formato::OBBLIGATORIO()),            ssf\getSingleField(DBT_VTR_VIS, ssf\Formato::NUMERO(), ssf\Formato::OBBLIGATORIO())        );        $fks = array(            ssf\getFKs(DBT_ID_VOC, DBT_VOC)        );        ssf\creaTabella(DBT_VTR, $args, $fks);        return true;            } catch (Exception $ex) {        _e($ex);        return false;    }}/** ELIMINAZIONE DATABASE **/function delete_etichette_db(): bool{    try{        //VOCE TRADOTTA        ssf\dropTabella(DBT_VTR);                //ETICHETTA        ssf\dropTabella(DBT_ETI);        //CLIENTE        ssf\dropTabella(DBT_CLI);        //CATEGORIA        ssf\dropTabella(DBT_CAT);        //VOCE        ssf\dropTabella(DBT_VOC);        //TEMPLATE        ssf\dropTabella(DBT_TEM);                  //TRADUZIONE        ssf\dropTabella(DBT_TRA);        return true;            } catch (Exception $ex) {        _e($ex);        return false;    }}