<?phpnamespace etichette;use seositiframework as ssf;/****************** CLIENTE ******************///Form salva clienteadd_shortcode('salvaCliente', 'addClienteSaveForm');function addClienteSaveForm(){    $view = new ClienteView();    $html = '';    $html .= $view->printSaveForm();    return $html;}//Listener Form Salva clienteadd_shortcode('listenerClienteForm', 'addListenerClienteForm');function addListenerClienteForm(){    $view = new ClienteView();    $html = '';    $html .= $view->listenerClienteForm();    return $html;}//Mostra clientiadd_shortcode('listaClienti', 'addListaClienti');function addListaClienti(){    $view = new ClienteView();    $html = '';    $html .= $view->printTabellaClienti();    return $html;}//Dettaglio clienteadd_shortcode('dettaglioCliente', 'addDettaglioCliente');function addDettaglioCliente(){    $html = '';    $id = ssf\getUrlGETInfo('ID');    if($id != null){        $view = new ClienteView();        $html .= $view->printDetailsForm($id);    }    else{        $html .= ssf\PrinterView::printErrorBoxMessage('Parametro non inserito o non riconosciuto.');    }    return $html;}/****************** CATEGORIA ******************///Form salva categoriaadd_shortcode('salvaCategoria', 'addCategoriaSaveForm');function addCategoriaSaveForm(){    $view = new EtichettaView();    $html = '';    $html .= $view->printCategoriaSaveForm();    return $html;}//Listener Form Categoriaadd_shortcode('listenerCategoriaForm', 'addListenerCategoriaForm');function addListenerCategoriaForm(){    $view = new EtichettaView();    $html = '';    $html .= $view->listenerCategoriaForm();    return $html;}//Mostra categorieadd_shortcode('listaCategorie', 'addListaCategorie');function addListaCategorie(){    $view = new EtichettaView();    $html = '';    $html .= $view->printTabellaCategorie();    return $html;}//Dettaglio categoriaadd_shortcode('dettaglioCategoria', 'addDettaglioCategoria');function addCategoriaDettaglio(){    $html = '';    $id = ssf\getUrlGETInfo('ID');        if($id != null){        $view = new EtichettaView();        $html .= $view->printCategoriaDetailsForm($id);    }    else{        $html .= ssf\PrinterView::printErrorBoxMessage('Parametro non inserito o non riconosciuto.');    }    return $html;}/****************** TEMPLATE ******************///Form salva templateadd_shortcode('salvaTemplate', 'addTemplateSaveForm');function addTemplateSaveForm(){    $view = new EtichettaView();    $html = '';    $html .= $view->printTemplateSaveForm();    return $html;}//Listener Form Templateadd_shortcode('listenerTemplateForm', 'addListenerTemplateForm');function addListenerTemplateForm(){    $view = new EtichettaView();    $html = '';    $html .= $view->listenerTemplateForm();    return $html;}//Mostra templateadd_shortcode('listaTemplate', 'addListaTemplate');function addListaTemplate(){    $view = new EtichettaView();    $html = '';    $html .= $view->printTabellaTemplate();    return $html;}//Dettaglio templateadd_shortcode('dettaglioTemplate', 'addDettaglioTemplate');function addDettaglioTemplate(){    $html = '';    $id = ssf\getUrlGETInfo('ID');    if($id != null){        $view = new EtichettaView();        $html .= $view->printTemplateDetailsForm($id);    }    else{        $html .= ssf\PrinterView::printErrorBoxMessage('Parametro non inserito o non riconosciuto');    }    return $html;}/****************** ETICHETTA ******************/add_shortcode('salvaEtichetta', 'addEtichettaSaveForm');function addEtichettaSaveForm(){    $view = new EtichettaView();    $html = '';    $html .= $view->printEtichettaSaveForm();    return $html;}add_shortcode('listenerEtichettaForm', 'addListenerEtichettaForm');function addListenerEtichettaForm(){    $view = new EtichettaView();    $html = '';    $html .= $view->listenerEtichettaForm();    return $html;}add_shortcode('listaEtichetta', 'addListaEtichetta');function addListaEtichetta(){    $view = new EtichettaView();    $html = '';    $html .= $view->printTabellaEtichette();    return $html;}function addDettaglioEtichetta(){    $html = '';    $id = ssf\getUrlGETInfo('ID');    if($id != null){        $view = new EtichettaView();        $html .= $view->printEtichettaDetailsForm($id);    }    else{        $html .= ssf\PrinterView::printErrorBoxMessage('Parametro non inserito o non riconosciuto');    }    return $html;}function addEtichetta(){    $html = '';    $url = ssf\getUrlGETInfo('url');    if($url != null){        $view = new EtichettaView();        $html .= $view->printEtichetta($url);    }    else{        $html .= ssf\PrinterView::printErrorBoxMessage('Qualcosa Ã¨ andato storto.');    }    return $html;}//Visualizza etichette per categoriaadd_shortcode('etichetteByCategoria', 'showEtichetteByCategoria');function showEtichetteByCategoria($attr){    $args = shortcode_atts(array('categoria' => ''), $attr);    $view = new EtichettaView();    $html = '';    $html .= $view->printEtichetteByNomeCategoria($args['categoria']);    return $html;}/****************** TRADUZIONI ******************/add_shortcode('attivaTraduzioni', 'addAttivaTraduzioni');function addAttivaTraduzioni(){    $html = '';    $view = new TraduzioneView();    $html .= $view->attivaTraduzioni();        return $html;}